import React from "react";
import { StateContext } from "../contexts";
import { useContext } from "react";
import ListItem from "./ListItem";
import styled from "styled-components";
import { useResource } from "react-request-hook"

const ListComponent = ({listItem} ) => {

  const {state, dispatch} = useContext(StateContext);

  const [todoList, changeToggle] = useResource(({title, description, author, complete, dateSet, id}) => {
    complete = !complete;
    return ({ 
    url: `/listItem/${id}`,
    method: "patch",
    data: {title, description, author, complete, dateSet, id},
  })
}
  );

  const checkBox = (title, description, author, complete, dateSet, id)=>{
    changeToggle(title, description, author, complete, dateSet, id)
    dispatch( {type: "CHECK_COMPLETE", payload: id} )
  }

  return (
    <Wrapper>
      {listItem.map((element, index) => {
        const {id, title, description, author, dateSet, complete, dateComplete} =
          element;

        const key = index

        return (
          <ListItem
            id={id} 
            key = {key}
            title={title}
            description={description}
            author={author}
            dateCreated={dateSet}
            complete={complete}
            dateComplete={dateComplete}
            handleCompletedBox={ ()=> checkBox(title, description, author, complete, dateSet, id) }
            handleDelete={ ()=> dispatch( {type: "DELETE_TODO", payload: id} ) }

          />
        );
      })}
    </Wrapper>
  );
};

export default ListComponent;

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
`;
