import "./App.css";
import ListComponent from "./components/ListComponent";
import { useReducer } from "react";
import UserBar from "./UserBar";
import CreateNewItem from "./components/CreateNewItem";
import appReducer from "./Reducers";
import { StateContext } from "./contexts";

function App() {

  const [state, dispatch] = useReducer(appReducer, {
    user: "",
    listItem: [],
  })

  const {listItem, user} = state;

  // const handleCompletedBox = (id) => {
  //   dispatch( {type: "CHECK_COMPLETE", payload: id} )
  // };

  // const handleNewListItem = (newItem) => {
  //   dispatch( {type: "CREATE_TODO", payload: {listItem:newItem,author:user} })
  // };

  // const handleDelete = (id) => {
  //   dispatch( {type: "DELETE_TODO", payload: id} )
  // }

  return (
    <div className="App">
      <StateContext.Provider value={{state, dispatch}}>
        <UserBar  dispatch={dispatch} />

        {user && <CreateNewItem dispatch={dispatch} />}

        {state.user && (
          <ListComponent
            listItem={listItem}
            user={user}
            dispatch={dispatch}
          />
        )}
      </StateContext.Provider>
    </div>
  );
}

export default App;
