import "./App.css";
import ListComponent from "./components/ListComponent";
import { useEffect, useReducer } from "react";
import { useResource } from "react-request-hook";
import UserBar from "./UserBar";
import CreateNewItem from "./components/CreateNewItem";
import appReducer from "./Reducers";
import { StateContext } from "./contexts";

function App() {

  // useEffect(()=> {
  //   fetch("/api/listItem")
  //     .then((result) => result.json())
  //     .then((listItem) => dispatch({ type: "FETCH_TODO", listItem }));
  // }, []);

  const [ toDoListItem, getToDoListItem ] = useResource(() => ({
    url: '/listItem',
    method: 'get'
  }))


  useEffect(() => {
    if (toDoListItem && toDoListItem.data){
      dispatch({ type: "FETCH_TODO", listItem: toDoListItem.data.reverse() })
    }
  }, [toDoListItem]);

  const [state, dispatch] = useReducer(appReducer, {
    user: "",
    listItem: [],
  })

  const {listItem, user} = state;

  useEffect(getToDoListItem, [user]);

  return (
    <div className="App">
      <StateContext.Provider value={{state, dispatch}}>
        <UserBar />
        {user && <CreateNewItem />}
        {state.user && (
          <ListComponent
            listItem={listItem}
            user={user}
            //dispatch={dispatch}
          />
        )}
      </StateContext.Provider>
    </div>
  );
}

export default App;
